/*!
**     @brief
**         Sets the edge type for this component that generates the
**         interrupt.
**     @param
**         DeviceDataPtr   - Device data structure
**                           pointer returned by <Init> method.
**     @param
**         Edge            - Edge type:
**                           0 - falling edge
**                           1 - rising edge
**                           2 - both edges
**                           3 - low level
**                           4 - high level
**     @return
**                         - Error code, possible codes:
**                           ERR_OK - OK
**                           ERR_RANGE - Value is out of range
*/
/* ===================================================================*/
LDD_TError ExtIntLdd3_SetEdge(LDD_TDeviceData *DeviceDataPtr, uint8_t Edge)
{
  ExtIntLdd3_TDeviceData *DeviceDataPrv = (ExtIntLdd3_TDeviceData *)DeviceDataPtr;
  uint32_t Config;

  /* Edge value test - this test can be disabled by setting the "Ignore range checking"
     property to the "yes" value in the "Configuration inspector" */
  if (Edge > 4U) {
    return ERR_RANGE;                  /* Invalid edge parameter */
  }
  switch (Edge) {
    case 0:                            /* Falling edge */
      Config = PORT_PDD_INTERRUPT_ON_FALLING;
      break;
    case 1:                            /* Rising edge */
      Config = PORT_PDD_INTERRUPT_ON_RISING;
      break;
    case 2:                            /* Both edges */
      Config = PORT_PDD_INTERRUPT_ON_RISING_FALLING;
      break;
    case 3:                            /* Low level */
      Config = PORT_PDD_INTERRUPT_ON_ZERO;
      break;
    case 4:                            /* High level */
      Config = PORT_PDD_INTERRUPT_ON_ONE;
      break;
    default:
      return ERR_RANGE;
  }
  /* Apply the new configuration */
  PORT_PDD_SetPinInterruptConfiguration(PORTA_BASE_PTR, ExtIntLdd3_PIN_INDEX, Config);
  /* Save the new IRQ configuration */
  DeviceDataPrv->IrqConfig = Config;
  return ERR_OK;
}

